import React, { useEffect } from "react";
import ScreenHeading from "../../utilities/ScreenHeading/ScreenHeading";
import ScrollService from "../../utilities/scrollService";
import Animations from "../../utilities/Animations";
import { VerticalTimeline, VerticalTimelineElement }  from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';
import { Icon } from '@iconify/react';



const Resume = (props) => {
    console.log("Resume"+ props)
  let fadeInScreenHandler = (screen) => {
    if (screen.fadeInScreen !== props.id) return;

    Animations.animations.fadeInScreen(props.id);
  };

  const fadeInSubscription =
    ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);


  useEffect(() => {
    return () => {
      /* UNSUBSCRIBE THE SUBSCRIPTIONS */
      fadeInSubscription.unsubscribe();
    };
  }, [fadeInSubscription]);

  return (
    <div
      className="resume-container screen-container fade-in"
      id={props.id || ""}
    >
      <div className="resume-content">
        <ScreenHeading
          title={" Experience"}
          subHeading={"My Work Adventure"}
        />
            <div className="bullet-container">

<VerticalTimeline 
  animate= {true}
  className="VerticalTimeline"
  layout={"1-column-left"}
  lineColor={" #1f2235"}

 >
  <VerticalTimelineElement
    className="vertical-timeline-element--work"
    // contentStyle={{ background: "#b5651d", color: '#fff' }}
    // contentArrowStyle={{ borderRight: '7px solid  rgb(33, 150, 243)' }}
    date="DEC 2021 - present"
    iconStyle={{ background: "#b5651d", color: '#fff',fontSize:'40px' }}
    icon={<Icon icon="bx:bxl-php" />}


  >
    <h4 className="vertical-timeline-element-title"style>WEB DEVELOPER || UF/IFAS </h4>
    <h5 className="vertical-timeline-element-subtitle">Gainesville, FL {`{USA}`}</h5>
    <p>
      Here Creative Direction, User Experience, Visual Design, Project Management, Team Leading
    </p>
  </VerticalTimelineElement>
  <VerticalTimelineElement
    className="vertical-timeline-element--work"
    // contentStyle={{ background: "#1f2235", color: '#fff' }}
    date="JULY2020 - AUGUST 2020"
    iconStyle={{ background: "#b5651d", color: '#fff' }}
    icon={<Icon icon="mdi:web" hFlip={true} vFlip={true} />}
  >
    <h4 className="vertical-timeline-element-title">WEB DEVELOPER || INTERNSHALA</h4>
    <h5 className="vertical-timeline-element-subtitle">Remote {`{INDIA}`} </h5>
    <p>
      Here
    </p>
  </VerticalTimelineElement>
  <VerticalTimelineElement
    className="vertical-timeline-element--work"
    // contentStyle={{ background: "#1f2235", color: '#fff' }}
    date="MAY 2020 - JUNE 2020"
    iconStyle={{ background: "#b5651d", color: '#fff' }}
    icon={<Icon icon="simple-icons:react" hFlip={true} vFlip={true} />}
  >
    <h4 className="vertical-timeline-element-title" >MOBILE APPLICATION DEVELOPER || FASTOR</h4>
    <h5 className="vertical-timeline-element-subtitle">New Delhi {`{INDIA}`}  </h5>
    <p>
      Here
    </p>
  </VerticalTimelineElement>


</VerticalTimeline>
              {/* <div className="bullet-icons"></div> */}
            </div>
          {/* </div> */}
        {/* </div> */}
      </div>
    </div>
  );
};

export default Resume;
