import React from "react";

import Modal from "react-modal";
import "./ModalIndex.css";
import { Icon } from "@iconify/react";

const customStyles = {
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(255, 255, 255, 0.75)",
  },
  content: {
    position: "absolute",
    top: "40px",
    left: "40px",
    right: "40px",
    // innerwidth: "500px",
    bottom: "40px",
    border: "1px solid #fff",
    background: "#fff",
    overflow: "auto",
    WebkitOverflowScrolling: "touch",
    borderRadius: "4px",
    outline: "none",
    // padding: '20px'
  },
  //   content: {
  //     top: "50%",
  //     left: "50%",
  //     right: "auto",
  //     bottom: "auto",
  //     backgroundColor: "Black",
  //     marginRight: "-50%",
  //     transform: "translate(-50%, -50%)",
  //   },
};

function App() {
  let subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "#f00";
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div>
      <div className="Container">
        <button className="Button" onClick={openModal}>
          <Icon className="Certificate" icon="mdi:certificate" />
          Certificate...
        </button>
      </div>

      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <h2 ref={(_subtitle) => (subtitle = _subtitle)}>CERTIFICATE</h2>

        <div className="ModalWrapper">
          <div className="Icon">

            <div className="Certificate">
              <img
                className="ModalImg"
                src={require("./certificateImg.jpg").default}
                alt="camera"
              />

            </div>
            <div className="Cross">
              <button  className= 'crossbutton'onClick={closeModal}>
                <i className="fa fa-times"></i>
              </button>
            </div>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default App;
