import React, { useEffect } from "react";
import ScreenHeading from "../../utilities/ScreenHeading/ScreenHeading";
import ScrollService from "../../utilities/scrollService";
import Animations from "../../utilities/Animations";
import {
  VerticalTimeline,
  VerticalTimelineElement,
} from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";
import { Icon } from "@iconify/react";
import "./project.css";

const Resume = (props) => {
  let fadeInScreenHandler = (screen) => {
    if(props === undefined) {return}
    if (screen.fadeInScreen !== props.id) return;

    Animations.animations.fadeInScreen(props.id);
  };

  const fadeInSubscription =
    ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);

  useEffect(() => {
    return () => {
      /* UNSUBSCRIBE THE SUBSCRIPTIONS */
      fadeInSubscription.unsubscribe();
    };
  }, [fadeInSubscription]);

  return (
    <div
      className="resume-container screen-container fade-in"
      id={props.id || ""}
    >
      <div className="resume-content">
        <ScreenHeading title={"Projects"} subHeading={"What I Develop"} />
        {/* <div className="resume-card"> */}
        {/* <div className="resume-bullets"> */}
        <div className="bullet-container">
          <VerticalTimeline
            animate={true}
            className="VerticalTimeline"
            layout={"1-column-left"}
            lineColor={" #1f2235"}
            //   #b5651d"}
            //   #1f2235
            //   "}
          >
            <VerticalTimelineElement
              className="vertical-timeline-element--work"
              // contentStyle={{ background: "#b5651d", color: '#fff' }}
              // contentArrowStyle={{ borderRight: '7px solid  rgb(33, 150, 243)' }}
              date="January 2022 - Current"
              iconStyle={{ background: "#b5651d", color: "#fff" }}
              icon={<Icon icon="bx:bxl-php" />}

            >
              <h3
                className="vertical-timeline-element-title"
                style={{ color: "#1F2235" }}
              >
                Customer Support {" "}
              </h3>
              <div className="Language">
                <p>Node.js, React.js, Redux, javascript, Express, MongoDB, HTML/CSS </p>
              </div>

              <p>
                <ul>
                  <li>Developing the website which provides the knowledge about the companies.</li>
                  <li>The application supports users for getting reviews about various companies and helps users with complaints and claims.</li>
                </ul>


              </p>

              <div className="lprofile-options">
                <a style={{ textDecoration: "none" }} href="/">
                  <button className="btn highlighted-btn">Source Code</button>
